
public class ArithmaticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1, num2, result;
		num1=10;
		num2=5;
		result=num1+num2;
		System.out.println("Result:"+result);
		System.out.println(num1+num2);
		System.out.println(num1-num2);
		System.out.println(num1*num2);
		System.out.println(num1/num2);
		System.out.println(num1%num2);
		System.out.println(num1+=num2);
		System.out.println(num1-=num2);
	}

}
